import React from 'react'
import Header from '../components/Header'
import Footer from '../components/Footer'
import { useState } from 'react'
import axios from 'axios'
import { Link } from 'react-router-dom'
import './CompilerPyCpp.css'
import EditorA from './EditorA'

function CompilerPyCpp() {
  const [code, setCode] = useState('')
  const [output, setOutput] = useState('')
  const [language, setLanguage] = useState('C++')
  const [theme, setTheme] = useState('eclipse')
  const handleSubmit = async () => {
    const payload = {
      language,
      code,
    }
    try {
      const { data } = await axios.post('http://localhost:13000/run', payload)
      setOutput(data.output)
      console.log(data.output)
    } catch ({ response }) {
      if (response) {
        const errorMsg = response.data.err.stderr
        setOutput(errorMsg)
        console.log(response)
      } else {
        setOutput('Error connecting to server')
      }
    }
  }
  return (
    <>
      <Header />
      <div className="home-ugsrp">
        <h2 className="header-text-ugsrp">
          Write, compile and run your C++ and Python code!
        </h2>
        <div className="block">
          <div>
            <label>Please choose your language: </label>
            <select
              value={language}
              onChange={(e) => {
                setLanguage(e.target.value)
                console.log(e.target.value)
              }}
            >
              <option value="C++">C++</option>
              <option value="Python">Python</option>
            </select>
          </div>
          <br />
          <EditorA
            language={language}
            displayName={language}
            value={code}
            onChange={setCode}
          />
          <br />
          <button className="btn-submit" onClick={handleSubmit}>
            Run
          </button>
          <p>{output}</p>
        </div>
      </div>
      <Footer />
    </>
  )
}

export default CompilerPyCpp
