import React from 'react'
import Header from '../components/Header'
import Footer from '../components/Footer'
import poster from '../images/poster.png'
import airq from '../images/airq.jpg'
import { useState } from 'react'
import axios from 'axios'
import { Link } from 'react-router-dom'
import './CompilerPyCpp.css'
import Editor from './Editor'

function CompilerPyCpp() {
  const [code, setCode] = useState('')
  const [output, setOutput] = useState('')
  const [language, setLanguage] = useState('cpp')
  const handleSubmit = async () => {
    const payload = {
      language,
      code,
    }
    try {
      const { data } = await axios.post('http://localhost:13000/run', payload)
      setOutput(data.output)
      console.log(data.output)
    } catch ({ response }) {
      if (response) {
        const errorMsg = response.data.err.stderr
        setOutput(errorMsg)
        console.log(response)
      } else {
        setOutput('Error connecting to server')
      }
    }
  }
  return (
    <>
      <Header />
      <div className="home-ugsrp">
        <h2 className="header-text-ugsrp">
          SONYC project at Undergraduate Summer Research Program 2021.
        </h2>
        <div className="block">
          <h1>Online Code Editor and Compiler</h1>
          <div>
            <label>Language: </label>
            <select
              value={language}
              onChange={(e) => {
                setLanguage(e.target.value)
                console.log(e.target.value)
              }}
            >
              <option value="cpp">C++</option>
              <option value="py">Python</option>
            </select>
          </div>
          <br />
          <Editor
            language={language}
            displayName={language}
            value={code}
            onChange={(e) => {
              setCode(e.target.value)
            }}


          />

          <textarea
            rows="35"
            cols="75"
            value={code}
            onChange={(e) => {
              setCode(e.target.value)
            }}
          ></textarea>
          <br />
          <button className="btn-submit" onClick={handleSubmit}>
            Submit
          </button>
          <p>{output}</p>
        </div>
      </div>
      <Footer />
    </>
  )
}

export default CompilerPyCpp
